            if (!mentionsData.data[newTweetIndex].tweet.stats) {
              mentionsData.data[newTweetIndex].tweet.stats = tweetData.stats;
            } else {
              mentionsData.data[newTweetIndex].tweet.stats.comments =
                tweetData.stats.comments;
            }
